def search_synonyms(synonyms_list, name):
    """this function searches through the sentences of the human aging corpus (20.4 million sentences) for synonyms to AHs and ARDs
    and it returns a csv file of all the sentences with mentions (i.e., exact matches of synonyms)"""
    all_sent = pd.DataFrame()
    for i in range(0, len(synonyms_list)):
        print(synonyms_list[i])
        specific_sent = [re.search(synonyms_list[i], a) is not None for a in df2.abstracts_decode.iloc[0:]]
        specific_sent = df2[specific_sent]
        all_sent = all_sent.append(specific_sent)
    all_sent = all_sent.drop_duplicates()
    all_sent.to_csv("data/AH_ARD_search/" + str(name) + "_sentences.csv")
    time.sleep(60)
    return(all_sent)


