def ochiai_coefficient(name, disease_table_GI, disease_table_TA, disease_table_EA, disease_table_LOP,
                      disease_table_DNS, disease_table_MD, disease_table_CS, disease_table_SCE, 
                      disease_table_AIC, GI_sent_new, TA_sent_new, EA_sent_new, LOP_sent_new, 
                      DNS_sent_new, MD_sent_new, CS_sent_new, SCE_sent_new, AIC_sent_new):
    """Returns the ochiai coefficient for aging hallmarks and ARDs"""
    Disease_Search = pd.read_csv("data/AH_ARD_search/" + str(name) + "_sentences.csv")
    Disease_Absolute_Count = int(Disease_Search.shape[0])
    GI_Disease_Overlap_Count = int(disease_table_GI[disease_table_GI.Disease==name].shape[0])
    TA_Disease_Overlap_Count = int(disease_table_TA[disease_table_TA.Disease==name].shape[0])
    EA_Disease_Overlap_Count = int(disease_table_EA[disease_table_EA.Disease==name].shape[0])
    LOP_Disease_Overlap_Count = int(disease_table_LOP[disease_table_LOP.Disease==name].shape[0])
    DNS_Disease_Overlap_Count = int(disease_table_DNS[disease_table_DNS.Disease==name].shape[0])
    MD_Disease_Overlap_Count = int(disease_table_MD[disease_table_MD.Disease==name].shape[0])
    CS_Disease_Overlap_Count = int(disease_table_CS[disease_table_CS.Disease==name].shape[0])
    SCE_Disease_Overlap_Count = int(disease_table_SCE[disease_table_SCE.Disease==name].shape[0])
    AIC_Disease_Overlap_Count = int(disease_table_AIC[disease_table_AIC.Disease==name].shape[0])
    GI_Absolute_Count = int(GI_sent_new.abstracts.shape[0])
    TA_Absolute_Count = int(TA_sent_new.abstracts.shape[0])
    EA_Absolute_Count = int(EA_sent_new.abstracts.shape[0])
    LOP_Absolute_Count = int(LOP_sent_new.abstracts.shape[0])
    DNS_Absolute_Count = int(DNS_sent_new.abstracts.shape[0])
    MD_Absolute_Count = int(MD_sent_new.abstracts.shape[0])
    CS_Absolute_Count = int(CS_sent_new.abstracts.shape[0])
    SCE_Absolute_Count = int(SCE_sent_new.abstracts.shape[0])
    AIC_Absolute_Count = int(AIC_sent_new.abstracts.shape[0])
    Disease_Ochiai_GI = sqrt((GI_Disease_Overlap_Count*GI_Disease_Overlap_Count)/(Disease_Absolute_Count*GI_Absolute_Count))
    Disease_Ochiai_TA = sqrt((TA_Disease_Overlap_Count*TA_Disease_Overlap_Count)/(Disease_Absolute_Count*TA_Absolute_Count))
    Disease_Ochiai_EA = sqrt((EA_Disease_Overlap_Count*EA_Disease_Overlap_Count)/(Disease_Absolute_Count*EA_Absolute_Count))
    Disease_Ochiai_LOP = sqrt((LOP_Disease_Overlap_Count*LOP_Disease_Overlap_Count)/(Disease_Absolute_Count*LOP_Absolute_Count))
    Disease_Ochiai_DNS = sqrt((DNS_Disease_Overlap_Count*DNS_Disease_Overlap_Count)/(Disease_Absolute_Count*DNS_Absolute_Count))
    Disease_Ochiai_MD = sqrt((MD_Disease_Overlap_Count*MD_Disease_Overlap_Count)/(Disease_Absolute_Count*MD_Absolute_Count))
    Disease_Ochiai_CS = sqrt((CS_Disease_Overlap_Count*CS_Disease_Overlap_Count)/(Disease_Absolute_Count*CS_Absolute_Count))
    Disease_Ochiai_SCE = sqrt((SCE_Disease_Overlap_Count*SCE_Disease_Overlap_Count)/(Disease_Absolute_Count*SCE_Absolute_Count))
    Disease_Ochiai_AIC = sqrt((AIC_Disease_Overlap_Count*AIC_Disease_Overlap_Count)/(Disease_Absolute_Count*AIC_Absolute_Count))
    Ochiai_frame = pd.DataFrame({'Disease_Ochiai_GI': [Disease_Ochiai_GI],'Disease_Ochiai_TA': [Disease_Ochiai_TA],
                                 'Disease_Ochiai_EA': [Disease_Ochiai_EA],'Disease_Ochiai_LOP': [Disease_Ochiai_LOP],
                                 'Disease_Ochiai_DNS': [Disease_Ochiai_DNS],'Disease_Ochiai_MD': [Disease_Ochiai_MD], 
                                 'Disease_Ochiai_CS': [Disease_Ochiai_CS],'Disease_Ochiai_SCE': [Disease_Ochiai_SCE], 
                                 'Disease_Ochiai_AIC': [Disease_Ochiai_AIC],'Disease_Absolute_Count': [Disease_Absolute_Count],
                                 'GI_Absolute_Count': [GI_Absolute_Count],'TA_Absolute_Count': [TA_Absolute_Count],
                                 'EA_Absolute_Count': [EA_Absolute_Count],'LOP_Absolute_Count': [LOP_Absolute_Count],
                                 'DNS_Absolute_Count': [DNS_Absolute_Count],'MD_Absolute_Count': [MD_Absolute_Count],
                                 'CS_Absolute_Count': [CS_Absolute_Count],'SCE_Absolute_Count': [SCE_Absolute_Count], 
                                 'AIC_Absolute_Count': [AIC_Absolute_Count]})
    Ochiai_frame = Ochiai_frame.transpose()
    Ochiai_frame.columns = [name]
    return(Ochiai_frame)


